definition.script:
pipeline {
    agent any

    environment {
        dockerImage = ''
        dockerHubId = 'docker-hub'
    }

    stages {
        stage('Git Clone') {
            steps {
                git url: 'https://github.com/kjb95/movie-rank-chart', branch: 'main', credentialsId: 'kjb'
            }
        }
        stage('Bulid Gradle') {
            steps {
                dir('backend') {
                    sh './gradlew build'
                }
            }
        }
        stage('Build Docker') {
            steps {
                dir('backend') {
                    script {
                        dockerImage = docker.build 'kjb95/my-spring-boot:1.0'
                    }
                }
            }
        }
        stage('Push Docker') {
            steps {
                script {
                    docker.withRegistry('', dockerHubId) {
                        dockerImage.push("1.0")
                    }
                }
            }
        }
        stage('SSH SERVER CONNECTION') {
            steps {
                sshagent(['ssh']) {
                    sh "ssh -p 3334 root@100.0.0.189 'docker pull kjb95/my-spring-boot:1.0'"
                    sh "ssh -p 3334 root@100.0.0.189 'docker run kjb95/my-spring-boot:1.0'"
                }
            }
        }
    }
}
