definition.script:
pipeline {
    agent any

    environment {
        dockerImage = ''
    }

    stages {
        stage('Git Clone') {
            steps {
                git url: 'https://github.com/kjb95/movie-rank-chart', branch: 'main', credentialsId: 'kjb'
            }
        }
        stage('Bulid Gradle') {
            steps {
                dir('backend') {
                    sh './gradlew build'
                }
            }
        }
        stage('Build Docker') {
            steps {
                script {
                    dockerImage = docker.build 'kjb95/my-base-image:1.0'
                }
            }
        }
    }
}
